        str <span class="token operator">=</span> str<span class="token punctuation">.</span><span class="token function">split</span><span class="token punctuation">(</span><span class="token string">''</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">reverse</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">reduce</span><span class="token punctuation">(</span><span class="token punctuation">(</span><span class="token parameter">pre<span class="token punctuation">,</span> next<span class="token punctuation">,</span> index</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span>
            <span class="token keyword">return</span> index <span class="token operator">></span> <span class="token punctuation">(</span>upindex <span class="token operator">+</span> <span class="token number">3</span><span class="token punctuation">)</span> <span class="token operator">&amp;&amp;</span> <span class="token operator">!</span><span class="token punctuation">(</span>index <span class="token operator">%</span> <span class="token number">3</span><span class="token punctuation">)</span> <span class="token operator">?</span> next <span class="token operator">+</span> <span class="token string">','</span> <span class="token operator">+</span> pre <span class="token operator">:</span> next <span class="token operator">+</span> pre
        <span class="token punctuation">}</span><span class="token punctuation">)</span>
        <span class="token keyword">return</span> str
    <span class="token punctuation">}</span>

